import CartContext from "./Store"
import React, { useReducer } from "react";


const defaultCartState = {
    items: [],
    totalAmount: 0
} 

const cartReducer = (state, action)=>{
    if(action.type === "ADD"){
        const updatedTotalAmount = state.totalAmount + action.item.price*action.item.amount;

        const existingItemIndex = state.item.findIndex(item=> item.id === action.item.id);
        const existingCartItem = state.item[existingItemIndex];
        let updatedItem;
        let updatedItems = []

        if(existingCartItem){
            updatedItems = {
                ...existingCartItem,
                amount: existingCartItem.amount + action.item.amount
               
        }
        updatedItems = [...state.items];
        updatedItems[existingItemIndex] = updatedItem;
        } else {
            updatedItem = {...action.item};
            const updatedItems = state.items.concat(action.item);
        }
       
        

        return{
            items: updatedItems,
            totalAmount: updatedTotalAmount
        }
    }
    if(action.type === "REMOVE"){

    }
}

const CartProvider = (props)=>{

    const [cartState, dispatchCardAction] = useReducer(cartReducer, defaultCartState );

    const addItemToCartHandler = (item)=>{
        dispatchCardAction({type: "ADD", item: item});
    }
    const removeItemFromCartHandler = (id)=>{
        dispatchCardAction({type: "REMOVE", id: id})
    }

    let cartContext = {
        items :cartState.items,
        totalAmount : cartState.totalAmount,
        addItem : addItemToCartHandler, 
        removeItem : removeItemFromCartHandler
    }

    return <CartContext.Provider value = {cartContext}>{props.children}</CartContext.Provider>;
}

export default CartProvider;
